import React, { useState, useEffect } from 'react';
import SpiroCanvas from '../components/SpiroCanvas';
import Controls from '../components/Controls';
import WalletConnect from '../components/WalletConnect';
import '../styles/components.css';

const Create = () => {
  // Spirograph parameters
  const [fixedRadius, setFixedRadius] = useState(150);
  const [movingRadius, setMovingRadius] = useState(40);
  const [offset, setOffset] = useState(80);
  const [strokeWeight, setStrokeWeight] = useState(2);
  const [strokeColor, setStrokeColor] = useState('#e3262e');

  // NFT and wallet state
  const [isRecording, setIsRecording] = useState(false);
  const [animationFrames, setAnimationFrames] = useState([]);
  const [isMinting, setIsMinting] = useState(false);
  const [isWalletConnected, setIsWalletConnected] = useState(false);
  const [hasToken, setHasToken] = useState(false);
  const [walletAddress, setWalletAddress] = useState(null);
  const [mintStatus, setMintStatus] = useState(null); // 'success', 'error', or null

  // Handle wallet connection
  const handleWalletConnect = (address, tokenOwnership) => {
    setWalletAddress(address);
    setIsWalletConnected(!!address);
    setHasToken(tokenOwnership);
  };

  // Reset canvas with new parameters
  const handleReset = () => {
    // Generate slightly different parameters for variety
    setFixedRadius(Math.floor(Math.random() * 100) + 100); // 100-200
    setMovingRadius(Math.floor(Math.random() * 70) + 20);  // 20-90
    setOffset(Math.floor(Math.random() * 80) + 40);       // 40-120
    setStrokeWeight(Math.floor(Math.random() * 3) + 1);   // 1-3

    // Reset animation and minting state
    setAnimationFrames([]);
    setIsRecording(false);
    setMintStatus(null);
  };

  // Handle animation completion
  const handleAnimationComplete = (frames) => {
    console.log(`Animation complete with ${frames.length} frames`);
    setAnimationFrames(frames);
  };

  // Mint NFT
  const handleMint = async () => {
    // Can't mint without wallet or token
    if (!isWalletConnected) {
      alert("Please connect your wallet first!");
      return;
    }

    if (!hasToken) {
      alert("You need the special token to mint for free!");
      return;
    }

    // Start recording if not already done
    if (animationFrames.length === 0) {
      setIsRecording(true);
      return; // Will trigger mint again after recording completes
    }

    try {
      setIsMinting(true);
      setMintStatus(null);

      // In a real implementation, this would upload frames to IPFS and mint an NFT
      console.log("Minting with parameters:", {
        fixedRadius,
        movingRadius,
        offset,
        strokeWeight,
        strokeColor,
        frames: animationFrames.length
      });

      // Simulate minting process
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Success!
      setMintStatus('success');

      // Prepare for next creation
      setTimeout(() => {
        handleReset();
      }, 3000);

    } catch (error) {
      console.error("Error minting NFT:", error);
      setMintStatus('error');
    } finally {
      setIsMinting(false);
      setIsRecording(false);
    }
  };

  // Handle animation recording effect
  useEffect(() => {
    if (animationFrames.length > 0 && isRecording) {
      handleMint();
    }
  }, [animationFrames, isRecording]);

  return (
    <div className="create-page">
      <div className="create-header">
        <h1>Create Your Spirograph NFT</h1>
        <p>Design your unique mathematical pattern and mint it as an animated NFT</p>
        <WalletConnect onConnect={handleWalletConnect} />
      </div>

      <div className="create-container">
        <div className="canvas-section">
          <SpiroCanvas
            fixedRadius={fixedRadius}
            movingRadius={movingRadius}
            offset={offset}
            strokeWeight={strokeWeight}
            strokeColor={strokeColor}
            isRecording={isRecording}
            onAnimationComplete={handleAnimationComplete}
          />

          {mintStatus === 'success' && (
            <div className="mint-success">
              <h3>Success! ðŸŽ‰</h3>
              <p>Your Spirograph NFT has been minted and sent to your wallet.</p>
              <p>Share your creation with friends!</p>
              <div className="share-buttons">
                <button className="share-twitter">Share on X</button>
                <button className="share-copy">Copy Link</button>
              </div>
            </div>
          )}

          {mintStatus === 'error' && (
            <div className="mint-error">
              <h3>Error Minting</h3>
              <p>There was a problem creating your NFT. Please try again.</p>
            </div>
          )}
        </div>

        <div className="controls-section">
          <Controls
            fixedRadius={fixedRadius}
            setFixedRadius={setFixedRadius}
            movingRadius={movingRadius}
            setMovingRadius={setMovingRadius}
            offset={offset}
            setOffset={setOffset}
            strokeWeight={strokeWeight}
            setStrokeWeight={setStrokeWeight}
            strokeColor={strokeColor}
            setStrokeColor={setStrokeColor}
            onReset={handleReset}
            onMint={handleMint}
            isMinting={isMinting || isRecording}
          />

          <div className="token-gate-info">
            <h3>Token Gate</h3>
            <p>This is a token-gated experience. You need to own our special token to mint NFTs for free.</p>
            {!isWalletConnected && (
              <p>Connect your wallet to check if you have the token.</p>
            )}
            {isWalletConnected && !hasToken && (
              <div>
                <p>You don't have the required token yet.</p>
                <button className="get-token-button">Get Token</button>
              </div>
            )}
            {isWalletConnected && hasToken && (
              <p className="token-success">You have the token! Mint as many NFTs as you want.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Create;
