        if (colorTransition >= 1) {
          startColorIndex = endColorIndex;
          endColorIndex = (endColorIndex + 1) % palette.colors.length;
          colorTransition = 0;
        }

        if (t > maxT) {
          p.noLoop(); // Stop drawing when complete
          setIsDrawing(false);

          // Notify parent that drawing is complete
          onDrawingComplete && onDrawingComplete(p.get());
        }

        p.pop();
      };

      // Generate a new random seed
      p.generateNewSeed = () => {
        currentSeed = Math.floor(p.random(1, 10001));
        setSeed && setSeed(currentSeed);
        resetSketch();
      };

      // Change to a specific seed
      p.setSeed = (newSeed) => {
        currentSeed = newSeed;
        resetSketch();
      };
    };

    // Create a new p5 instance
    p5Instance.current = new p5(sketch, canvasRef.current);

    // Cleanup function
    return () => {
      if (p5Instance.current) {
        p5Instance.current.remove();
      }
    };
  }, [seed]); // Re-initialize when seed changes

  // Method to generate a new seed
  const generateNewSeed = () => {
    if (p5Instance.current && p5Instance.current.generateNewSeed) {
      p5Instance.current.generateNewSeed();
    }
  };

  // Method to set a specific seed
  const setSeedValue = (newSeed) => {
    if (p5Instance.current && p5Instance.current.setSeed) {
      p5Instance.current.setSeed(newSeed);
    }
  };

  return (
    <div className="spiro-canvas-container">
      <div ref={canvasRef} className="canvas-wrapper"></div>
      {!isDrawing && (
        <div className="canvas-complete">
          <span>Drawing complete!</span>
        </div>
      )}
    </div>
  );
};

export default SpiroCanvas;
